import React, { useState } from 'react'
import './App.css'

export default function App() {
  const [changeBolder,setchangeBolder] = useState('true');
  const changeHandler = ()=>{
    setchangeBolder(false);
  }

  return (
    <div>
        <p className={`p1 ${changeBolder?"":'blueBolder'}`}>我是段落</p>
        <button onClick={changeHandler}>click me</button>
    </div>
  )
}
